                        return EventCodeEnum.Chuck_Vacuum_Error;
                    }


                    vacRet = CheckWaferStatus(true);

                    if (vacRet != EventCodeEnum.NONE)
                    {
                        LoggerManager.Debug($"Error CheckWaferStatus(true) after ThreeLegDown. retValue:{vacRet}");
