                {
                    ret = Module.MotionManager().StageMove(xpos, ypos, curZpos, 0);
                    ResultValidate(MethodBase.GetCurrentMethod(), ret);

                    ret = Module.MotionManager().AbsMove(zaxis, zpos, zaxis.Param.Speed.Value, zaxis.Param.Acceleration.Value);
